import React from 'react';
import { Card, CardContent, Typography, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';

const TaskCard = ({ task, onDeleteTask, onCompleteTask }) => {
  return (
    <Card style={{ marginBottom: '15px', backgroundColor: '#ffffff', borderRadius: '8px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)' }}>
      <CardContent>
        <Typography variant="h6" component="div" style={{ marginBottom: '10px' }}>
          {task.name}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          {task.description}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Usuario: {task.user}
        </Typography>
        <Typography variant="body2" color="textSecondary" component="p">
          Proyecto: {task.project}
        </Typography>
        <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '10px' }}>
          <IconButton aria-label="complete" onClick={() => onCompleteTask(task.id)}>
            <CheckCircleIcon color={task.completed ? "primary" : "disabled"} />
          </IconButton>
          <IconButton aria-label="delete" onClick={() => onDeleteTask(task.id)}>
            <DeleteIcon color="error" />
          </IconButton>
        </div>
      </CardContent>
    </Card>
  );
};

export default TaskCard;
